import logo from './logo.svg';
import './App.css';
import hic from './img/hic.jpg';
import styled from 'styled-components'
import moonlight from './img/moonlight.png';
import Illustrations from './Illustrations.js';
import {Link,useParams} from "react-router-dom";

function Detail(props) {
  const {src} = useParams();
  return (
    <div style={{
      height:"100vh",
      margin:"0",
      display: "flex",
      alignContent: "space-between",
      flexWrap: "wrap"
      }}>
      <div style={{
          fontSize: "40px",
          display: "flex",
          justifyContent: "space-between",
          padding:"20px 30px 0 30px",
          width:"100vw"
          }}>
          <h1 style={{
          fontSize: "40px",
          margin:"0"
          }}>
            {props.title}
          </h1>
          <p style={{
          margin:"0"
          }}>×</p>
      </div>
      <div style={{
          display: "flex",
          justifyContent: "center",
          alignContent:"center",
          width:"100vw"
        }}>
        <img style={{  
            height: "810px",
            }}
            src={src}/>
      </div>
      <ul style={{
          fontSize: "30px",
          display: "flex",
          justifyContent: "space-between",
          padding:"10px 20px",
          width:"100vw"
          }}>
          <li > ＜ </li>
          <li> ＞ </li>
      </ul>
    </div>
  );
}

export default Detail;
